import { useState, useCallback } from 'react'
import Counter from './components/Counter'
import './App.css'

function App() {
  const ref = useRef()
  const [counters, setCounters] = useState([
    {
      data: {},
      value: 0,
    },
  ])

  const onDelete = (index) => {
    const list = [...counters]
    list.splice(index, 1)
    setCounters(list)
  }

  const add = () => {
    setCounters([
      ...counters,
      {
        data: <Counter onDelete={onDelete} />,
        value: 0,
      },
    ])
  }

  return (
    <div className="App">
      <button onClick={add}>add</button>
      <button onClick={() => setCounters([])}>reset</button>
      <div>колличество счетчиков {counters.length}</div>
      <div className="counters">
        {counters.map((i, index) => (
          <Counter
            ref={ref}
            key={Math.random()}
            onDelete={onDelete}
            index={index}
            data={counters.data}
            value={i.value}
            setCounters={setCounters}
          />
        ))}
      </div>
    </div>
  )
}

export default App
