  const [counters, setCounters] = useState([
    {
      value: 0,
      component: {},
    },
  ])
  // const [value, setValue] = useState(0)

  const onDelete = (id) => {
    console.log(id)
    console.log(counters)
  }

  const add = () => {
    setCounters([
      ...counters,
      {
        component: (
          <Counter key={Date.now()} onDelete={onDelete} id={Date.now()} />
        ),
        value: 0,
      },
    ])
    console.log(counter)
  }

  return (
    <div className="App">
      <button onClick={add}>add</button>
      <button onClick={() => setCounters([])}>reset</button>
      <div>колличество счетчиков {counters.length}</div>
      {/* <div className="counters">{counters}</div> */}
    </div>
  )
}

export default App
