import { useState, useCallback, useRef } from 'react'
import Counter from './components/Counter'
import './App.css'
import { useEffect } from 'react/cjs/react.development'

function App() {
  const [counters, setCounters] = useState([])
  const [total, setTotal] = useState(0)

  const onDelete = (id) => {
    const list = [...counters]
    const index = list.findIndex((item) => item.id === id)
    list.splice(index, 1)
    list.forEach((item, i) => {
      if (item.value % 2 === 1) {
        list[i].value--
      }
    })
    setCounters(list)
  }

  const add = () => {
    const list = [...counters, { value: 0, id: new Date().getTime() }]
    // const list = [...counters]
    console.log(list)
    list.forEach((item, i) => {
      if (item.value % 2 === 1) {
        console.log('yes')
        list[i].value--
      }
    })
    setCounters(list)
  }

  const increment = (id) => {
    const list = [...counters]
    const index = list.findIndex((item) => item.id === id)
    list[index].value++
    setCounters(list)
  }

  const decrement = (id) => {
    const list = [...counters]
    const index = list.findIndex((item) => item.id === id)
    list[index].value--
    setCounters(list)
  }

  const resultFunc = () => {
    const res = counters.reduce(
      (sum, currentValue) => sum + currentValue.value,
      0
    )
    setTotal(res)
  }

  useEffect(() => {
    resultFunc()
  }, [counters])

  return (
    <div className="App">
      <button onClick={add}>add</button>
      <button onClick={() => setCounters([])}>reset</button>
      <div>количество счетчиков {counters.length}</div>
      <div>общий счет {total}</div>
      <div className="counters">
        {counters.map((item, index) => (
          <div className="content" key={item.id}>
            <Counter
              id={item.id}
              value={item.value}
              increment={increment}
              decrement={decrement}
            />

            <button onClick={() => onDelete(item.id)}>delete</button>
          </div>
        ))}
      </div>
    </div>
  )
}

export default App
