import { useState, useCallback, useRef } from 'react'
import Counter from './components/Counter'
import './App.css'

function App() {
  const ref = useRef()
  const [counters, setCounters] = useState([
    {
      data: {},
      value: 0,
    },
  ])

  const onDelete = (index) => {
    const list = [...counters]
    list.splice(index, 1)
    setCounters(list)
  }

  const add = () => {
    setCounters([
      ...counters,
      {
        data: <Counter onDelete={onDelete} />,
        value: 0,
      },
    ])
  }

  return (
    <div className="App">
      <button onClick={add}>add</button>
      <button onClick={() => setCounters([])}>reset</button>
      <div>колличество счетчиков {counters.length}</div>
      <div className="counters">{counters}</div>
    </div>
  )
}

export default App

