import { useState, useCallback } from 'react'
import { Counter } from './components/Counter'
import './App.css'

function App() {
  const [counters, setCounters] = useState([
    {
      data: {},
      value: 0,
    },
  ])

  const onDelete = (value) => {
    console.log(value)
  }

  const add = () => {
    setCounters([
      ...counters,
      {
        data: <Counter onDelete={onDelete} key={Math.random()} />,
        value: 0,
      },
    ])
    console.log(counters)
  }

  return (
    <div className="App">
      <button onClick={add}>add</button>
      <button onClick={() => setCounters([])}>reset</button>
      <div>колличество счетчиков {counters.length}</div>
      <div className="counters">
        {counters.map((i, index) => (
          <Counter index={index} data={counters.data} value={i.value} />
        ))}
      </div>
    </div>
  )
}

export default App
