import { useRef, useState } from 'react'
import { Counter } from './components/Counter'
import './App.css'

function App() {
  const [counters, setCounters] = useState([
    {
      data: {},
      value: 0,
      plusOne,
    },
  ])
  const [value, setValue] = useState(0)
  const ref = useRef()

  const onDelete = (id) => {
    console.log(id)
    console.log(counters)
  }

  const plusOne = (result, setResult) => {
    if (result % 2) {
      setResult(result + 1)
    }
    console.log(result)
  }

  const add = () => {
    setCounters([
      ...counters,
      {
        data: (
          <Counter
            key={Date.now()}
            plusOne={plusOne}
            onDelete={onDelete}
            id={Date.now()}
          />
        ),
      },
    ])
    console.log(counters)
  }

  return (
    <div className="App">
      <button ref={ref} onClick={add}>
        add
      </button>
      <button onClick={() => setCounters([])}>reset</button>
      <div>колличество счетчиков {counters.length}</div>

      {/* <div className="counters">{counters}</div> */}
    </div>
  )
}

export default App
